#include "node/Node.hpp"
#include <iostream>

int main()
{
    aodv::Node x1 = aodv::Node(0, "X1", "");
    aodv::Node x2 = aodv::Node(0, "X2", "");

    aodv::Eth e;
    e.src = "X1";
    e.srcLength = e.src.length();
    e.dst = "X2";
    e.dstLength = e.dst.length();
    // 64*32 = 2048 octets
    e.payload = "";
    e.payloadLength = e.payload.size();

    std::vector<std::string> v = x1.send(e, true);
    for (std::string s : v) {
        tl::optional<aodv::Eth> e = x2.receive(s);
        if (e) {
            aodv::Eth eth = e.value();
            std::cout << "FULL PACKET" << std::endl
                // << eth.seq << std::endl
                // << eth.dst << std::endl
                // << eth.dstlength << std::endl
                // << eth.src << std::endl
                // << eth.srclength << std::endl
                << eth.payloadLength << std::endl
                << eth.payload << std::endl;
        } else {
            std::cout << "EMPTY PACKET" << std::endl;
        }
    }
    return 0;
}
